import React, { useState } from 'react';

function App() {
  const [video1, setVideo1] = useState('');
  const [video2, setVideo2] = useState('');
  const [processedVideoUrl, setProcessedVideoUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  const handleProcessVideo = async () => {
    setLoading(true);
    setErrorMsg('');
    setProcessedVideoUrl(null);

    try {
      const response = await fetch('http://localhost:5040/process-video', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ video1, video2 }),
      });

      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.message || 'Error processing the video');
      }

      const data = await response.json();
      if (data.status === 'success') {
        setProcessedVideoUrl(data.processed_video_url);
      } else {
        throw new Error(data.message || 'Unknown error');
      }
    } catch (error) {
      console.error('Error:', error);
      setErrorMsg(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Trigger a browser download of the processed MP4
  const handleDownload = () => {
    if (!processedVideoUrl) return;

    const link = document.createElement('a');
    link.href = processedVideoUrl;
    link.download = 'processed-video.mp4'; // suggested filename
    link.click();
  };

  return (
    <div className="flex flex-col min-h-screen p-6">
      {/* Header */}
      <header className="mb-8">
        <h1 className="text-4xl font-bold mb-2 tracking-wide">
          Video Processing App
        </h1>
        <p className="text-gray-400">
          Enter two video URLs to concatenate and scale.
        </p>
      </header>

      {/* Form Section */}
      <div className="max-w-xl w-full mb-6">
        <label className="block mb-1 text-sm font-semibold text-gray-300">
          Video 1 URL
        </label>
        <input
          type="text"
          value={video1}
          onChange={(e) => setVideo1(e.target.value)}
          className="w-full p-2 mb-4 rounded outline-none text-gray-900"
          placeholder="https://cdn.shopify.com/videos/c/vp/..."
        />

        <label className="block mb-1 text-sm font-semibold text-gray-300">
          Video 2 URL
        </label>
        <input
          type="text"
          value={video2}
          onChange={(e) => setVideo2(e.target.value)}
          className="w-full p-2 mb-4 rounded outline-none text-gray-900"
          placeholder="https://cdn.shopify.com/videos/c/vp/..."
        />

        {/* Process + Download Buttons */}
        <div className="flex items-center gap-4 mt-4">
          <button
            onClick={handleProcessVideo}
            disabled={loading || !video1 || !video2}
            className={`${
              loading || !video1 || !video2
                ? 'bg-gray-600 cursor-not-allowed'
                : 'bg-teal-600 hover:bg-teal-700'
            } text-white px-5 py-2 font-semibold rounded transition`}
          >
            {loading ? 'Processing...' : 'Process Videos'}
          </button>

          {processedVideoUrl && (
            <button
              onClick={handleDownload}
              className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 font-semibold rounded transition"
            >
              Download Video
            </button>
          )}
        </div>
      </div>

      {/* Error Message */}
      {errorMsg && (
        <div className="text-red-400 font-semibold mt-4">
          Error: {errorMsg}
        </div>
      )}

      {/* Processed Video Preview */}
      {processedVideoUrl && (
        <div className="mt-10">
          <h3 className="text-2xl font-bold mb-3">Processed Video Preview</h3>
          <video
            src={processedVideoUrl}
            controls
            className="border border-gray-600 rounded w-full max-w-md"
          />
        </div>
      )}

      {/* Footer (optional) */}
      <footer className="mt-auto pt-8 text-center text-xs text-gray-600">
        &copy; {new Date().getFullYear()} Video Processing Demo
      </footer>
    </div>
  );
}

export default App;
