import React, { useState } from 'react';

function App() {
  const [video1, setVideo1] = useState('');
  const [video2, setVideo2] = useState('');
  const [processedVideoUrl, setProcessedVideoUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  const handleProcessVideo = async () => {
    setLoading(true);
    setErrorMsg('');
    setProcessedVideoUrl(null);

    try {
      const response = await fetch('http://localhost:5040/process-video', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ video1, video2 }),
      });

      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.message || 'Error processing the video');
      }

      const data = await response.json();
      if (data.status === 'success') {
        setProcessedVideoUrl(data.processed_video_url);
      } else {
        throw new Error(data.message || 'Unknown error');
      }
    } catch (error) {
      console.error('Error:', error);
      setErrorMsg(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Trigger a browser download of the processed MP4
  const handleDownload = () => {
    if (!processedVideoUrl) return;

    // Create a hidden anchor and click it programmatically
    const link = document.createElement('a');
    link.href = processedVideoUrl;
    link.download = 'processed-video.mp4'; // suggested file name
    link.click();
  };

  return (
    <div className="max-w-3xl mx-auto p-8">
      <h1 className="text-3xl font-bold mb-4">Video Processing App</h1>
      <p className="mb-6 text-gray-700">
        Enter two video URLs to concatenate and scale.
      </p>

      <div className="mb-4">
        <label className="block mb-1 font-medium">Video 1 URL:</label>
        <input
          type="text"
          value={video1}
          onChange={(e) => setVideo1(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded"
          placeholder="https://cdn.shopify.com/videos/c/vp/..."
        />
      </div>

      <div className="mb-4">
        <label className="block mb-1 font-medium">Video 2 URL:</label>
        <input
          type="text"
          value={video2}
          onChange={(e) => setVideo2(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded"
          placeholder="https://cdn.shopify.com/videos/c/vp/..."
        />
      </div>

      {/* Process Button */}
      <button
        onClick={handleProcessVideo}
        disabled={loading || !video1 || !video2}
        className={`${
          loading || !video1 || !video2
            ? 'bg-gray-400 cursor-not-allowed'
            : 'bg-blue-600 hover:bg-blue-700'
        } text-white px-4 py-2 rounded mr-4 transition`}
      >
        {loading ? 'Processing...' : 'Process Videos'}
      </button>

      {/* Download Button (only shows when there's a processed video) */}
      {processedVideoUrl && (
        <button
          onClick={handleDownload}
          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded transition"
        >
          Download Video
        </button>
      )}

      {/* Error Message */}
      {errorMsg && (
        <div className="mt-6 text-red-600">
          <strong>Error:</strong> {errorMsg}
        </div>
      )}

      {/* Processed Video Preview */}
      {processedVideoUrl && (
        <div className="mt-6">
          <h3 className="text-xl font-semibold mb-2">Processed Video Preview</h3>
          <video
            src={processedVideoUrl}
            controls
            className="border rounded max-w-full"
          />
        </div>
      )}
    </div>
  );
}

export default App;
